import { useEffect, useState, useReducer } from "react";
import Item from "./Item";
import { getAsyncStories, withError} from "./../../api/bbdd"

const initialStoriesState = {
  loading: false,
  error: false,
  stttories: []
}

const storiesReducer = (state, action) => {
  switch(action.type){
    case "STORIES_FETCH__INIT":
      return{
        loading: true,
        error: false,
        stories: []
      }
    case "STORIES_FETCH__SUUCCESS":
      return{
        loading: false,
        error: false,
        stories: action.payload
      }
    default:
      throw Error("actioon stories error")
  }
}



function List(props) {

  useEffect(() => {
    loadStories();
  }, []);
  const [list, setList] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isError, setIsError] = useState(false);
  const [storiesState, dispatchStories] = useReducer(storiesReducer, initialStoriesState);

  function loadStories(){
    dispatchStories({type: "STORIES_FETCH__INIT"})
    withError(getAsyncStories()).then(result => {
      dispatchStories({type: "STORIES_FETCH__SUC"})
      setList(result.data.stories);
      setIsLoading(false);
    }).catch(_err => setIsError(true));
  }

  if(isError){
    return <>
      <p>Error!</p>
      <button onClick={loadStories}>Retry</button>
    </>
  }

  return isLoading ? <p>Loading...</p> : 
    <div className="second-products">
      {list.map(function (item){
        return(
            <div key={item.ObjectID} className="second-products__card">
              <Item item={item} second={true} addCart={props.addCart}/>
            </div>
          );
        })}
    </div>
}



export default List;
