import FirstSection from './FirstSection/FirstSection';
import SecondSection from './SecondSection/SecondSection';
import Footer from './Footer/Footer';

function Tienda() {

//reducer de cesta-------------------------------------------------
const [state, dispatch] = useReducer(reducerCart, initialState);
