const addCart = (item) => {
  dispatch ({
    type: TYPES.add,payload:item,
  });
}
const delCart = (item) => {
  dispatch ({
    type: TYPES.del,payload:item,
  });
}
console.log(state)

localStorage.setItem('cart',JSON.stringify(state));


//reducer de cesta-------------------------------------------------

