import {useState, useReducer} from 'react';

const initialState = {contador:0};

const TYPES = {
  suma:   "suma",
  resta:  "resta",
};

function reducer(state, action){
  switch(action.type){
    case TYPES.suma:
      return {contador: state.contador +1};
    case TYPES.resta:
      return {contador: state.contador -1};
    default:
      return state;
  }
}

function Cart(props) {

  const [count, setCount] = useState(0);

  return (
    <div className="cart">
      <div className="cart-title">
        <p>Cart</p>
        <button className='cart-title_close' onClick={() => props.setCart(!props.cart)}>&#215;</button>
      </div>
      <div className="cart-products">
        {list.map(function (item){
          return(
            <div key={item.ObjectID} className="cart-products__card">
              <img src={item.img}></img>
              <a href={item.url}>{item.name}</a>
              <p aling="right">x{count}</p>
              <span>{item.price}€</span>
              <div className='mod-cant'>
                <button onClick={() => dispatch({type:TYPES.sumar})}>+</button>
                <button onClick={() => setCount(count - 1)}>-</button>
              </div>
            </div>
          );
        })}
      </div>
      <div className="cart-price">
        <p>Total:</p>
        <span></span>
      </div>
    </div>
  );
}

export default Cart;

























