import { useState } from "react";

import {useReducer} from "react";
import {reducerCart, initialState} from "./../../reducers/cartReducer";
import { TYPES } from "./../../reducers/cartReducer";

import Choice from "./Choice";
import Title from "./Title";
import Offer from "./Offer";
import Popup from "./Popup/Popup";
import Cart from "./Cart/Cart";

function Header() {

  const [popup, setPopup] = useState(false);
  const [cart, setCart] = useState(false);

  const [state, dispatch] = useReducer(reducerCart, initialState);
  const addCart = (ObjectID) => {
    dispatch ({
      type: TYPES.add,payload:ObjectID
    })
  }

  return (
    <>
      {popup && <Popup popup={popup} setPopup={setPopup} addCart={addCart}/>}
      <header className="header">
        <p className="header-T"><b>G</b>aming <b>S</b>hop</p>

        <Choice />

        <Title popup={popup} setPopup={setPopup}  cart={cart} setCart={setCart}/>

        {cart && <Cart cart={cart} setCart={setCart}/>}

        <Offer />
      </header>
    </>
  );
}

export default Header;
