import CartItem from "./CardItem";

function Cart(props) {

  //const [state, dispatch] = useReducer(reducerContador, initialState);

  return (
    <div className="cart">
      <div className="cart-title">
        <p>Cart</p>
        <button className='cart-title_close' onClick={() => props.setCarrito(!props.carrito)}>&#215;</button>
      </div>
      <div className="cart-products">
        {list.filter(item => item.ObjectID.includes(initialState.cart.ObjectID)).map(function (item){
          return(
            <div key={initialState.cart.ObjectID}>
              <CartItem item={item}/>
            </div>
          );
        })}
      </div>
      <div className="cart-price">
        <p>Total:</p>
        <span></span>
      </div>
    </div>
  );
}

export default Cart;
























