import { useState ,useEffect } from "react";
import CardItem from "./CardItem";

function Cart(props) {
  const local = JSON.parse(localStorage.getItem('cart'));

  const [count, setCount] = useState(0);

  useEffect(() =>{
    const total = local.map(function(item){
      const price = item.price
      const cant = item.quantity
      return price * cant
    })
    let suma
    console.log(total[1])
    setCount(total[1])
  },[local]);

  return (
    <div className="cart">
      <div className="cart-title">
        <p>Cart</p>
        <button className='cart-title_close' onClick={() => props.setCarrito(!props.carrito)}>&#215;</button>
      </div>

      <div className="cart-products">
        {local.map(function (item){
          return (
            <div key={item.ObjectID} className='cart-products__card'>
              <CardItem item={item} addCart={props.addCart} delCart={props.delCart}/>
            </div>
          );
        })}
      </div>

      <div className="cart-price">
        <span>Total: {count}</span>
      </div>
    </div>
  );
}

export default Cart;
